mapper.select:
SELECT *
FROM member
WHERE loginId = #{loginId}
AND loginPw =
#{loginPw}
AND delStatus = 0

SELECT *
FROM
member
WHERE name = #{name}
AND email = #{email}

SELECT *
FROM
member
WHERE name = #{name}
AND loginId = #{loginId}
AND email =
#{email}

SELECT COUNT(*) FROM `member`
WHERE loginId = #{loginId} OR
email = #{email}

mapper.update:
UPDATE member
name = #{name}, email = #{email},
WHERE id = #{id}

mapper.select:
SELECT * FROM `member`
WHERE email = #{email}
AND
emailAuthKey =
#{authKey}
